let clock=setInterval(()=>{let t=new Date,e=document.querySelector(".date"),n=document.querySelector(".hours"),o=document.querySelector(".minutes"),s=document.querySelector(".secondes");parseInt(e.textContent)!=t.getDate()&&(t.getDate()<10?e.textContent="0"+t.getDate():e.textContent=t.getDate()),parseInt(n.textContent)!=t.getHours()&&(t.getHours()<10?n.textContent="0"+t.getHours():n.textContent=t.getHours()),parseInt(o.textContent)!=t.getMinutes()&&(t.getMinutes()<10?o.textContent="0"+t.getMinutes():o.textContent=t.getMinutes()),parseInt(s.textContent)!=t.getSeconds()&&(t.getSeconds()<10?s.textContent="0"+t.getSeconds():s.textContent=t.getSeconds())},1e3);