let aside=document.querySelector(".aside"),btn=document.querySelectorAll(".btn-menu");btn.forEach(e=>{e.addEventListener("click",()=>{aside.classList.toggle("apparition")})});let nav=document.querySelector(".nav-bg"),ancre=document.querySelector(".anchor-menu"),logo=document.getElementById("logo"),navTop=nav.offsetTop;window.addEventListener("scroll",()=>{window.scrollY>navTop?(nav.classList.add("sticky"),ancre.classList.add("sticky"),logo.setAttribute("src","/img/logo2.png")):(nav.classList.remove("sticky"),ancre.classList.remove("sticky"),logo.setAttribute("src","/img/logo.png"))});let div1=document.querySelectorAll(".div1 div"),div2=document.querySelectorAll(".div2 div"),menu=document.querySelectorAll(".fa-caret-up"),selectMenu=document.querySelectorAll(".select-menu"),input=document.querySelectorAll(".selection");function openClose(e,t){e.addEventListener("click",()=>{selectMenu[t].classList.toggle("show"),menu[t].classList.toggle("rotate")})}div1.forEach(e=>{e.addEventListener("click",()=>{input[0].value=e.textContent})}),div2.forEach(e=>{e.addEventListener("click",()=>{input[1].value=e.textContent})});for(let e=0;e<menu.length;e++)openClose(menu[e],e),openClose(input[e],e),openClose(selectMenu[e],e);let load=document.querySelector(".loader");setInterval(()=>{load.style.display="none"},500);